contracts/Bank.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/extensions/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface IBank {
    function addFunds(address _addr, uint256 _amount) external ;
    function subFunds(address _addr, uint256 _amount) external ;
    function deposit() external payable ;
    function withDraw(uint256 _amount) external ;
    function balanceOf(address _addr) external returns(uint256);
}
contract Bank is Ownable{
    // bytes32 public constant ADMIN = keccak256("ADMIN");
    // bytes32 public constant OPERATOR = keccak256("OPERATOR");

    mapping(address => uint256) private funds;
    address[] public allowedCallers;

    event WithDraw(address _addr, uint256 _amount);
    event Deposit(address _addr, uint256 _amount);

    constructor() Ownable(msg.sender){
        //  ADMIN  OPERATOR 
        // _setRoleAdmin(OPERATOR, ADMIN);
        // sender
        // _grantRole(ADMIN, msg.sender);
    }

    function setAllowCaller (address _allowedCaller) public onlyOwner() {
        allowedCallers.push(_allowedCaller);
    }

    modifier onlyAllowedCaller() {
        //require(msg.sender == allowedCaller, "Access denied: Caller is not the allowed contract");
        bool allow = false;
        for(uint8 i=0; i<allowedCallers.length;i++){
            if(msg.sender == allowedCallers[i])allow=true;
        }
        require(allow, "Access denied: Caller is not the allowed contract");
        _;
    }
    function addFunds(address _addr, uint256 _amount) public onlyAllowedCaller(){// onlyRole(ADMIN) { 
        funds[_addr] = funds[_addr] + _amount;
    }
    function subFunds(address _addr, uint256 _amount) public  onlyAllowedCaller(){//onlyRole(ADMIN) {
        funds[_addr] = funds[_addr] - _amount;
    }
    function balanceOf(address _addr) external view returns(uint256) {
        return funds[_addr];  
    }
    function withDraw(uint256 _amount) external {
        require(_amount>0 ,"amount to small");
        require(funds[msg.sender]>_amount,"no enougth amount");
        funds[msg.sender] -= _amount;
        payable(msg.sender).transfer(_amount);
        emit WithDraw(msg.sender, _amount);
    }
    function deposit() external payable{
        require(msg.value>0 ,"amount to small");
        funds[msg.sender] += msg.value;
        emit Deposit(msg.sender, msg.value);
    }
}

contracts/ChuckALuck.sol.content:
pragma solidity ^0.8.20;

import "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBase.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./Bank.sol";

contract ChuckALuck is VRFConsumerBase{
    struct Bet {
        address beter;
        uint256 bet_time;
        uint256 bet_num;
        uint256 bet_amounts;
    }
    event WinFund(address addr,uint256 amounts);
    // Bet[] bets;
    mapping(bytes32=>Bet) public bets;
    uint256[][] public rolleds;

    Bank public bank;
    bytes32 internal _keyHash;
    uint internal _fee;
    function getRolledCount() public view returns (uint256){
        return rolleds.length;
    }
    function getRoll(uint256 _index) public view returns(uint256,uint256,uint256){
        return (rolleds[_index][0],rolleds[_index][1],rolleds[_index][2]);
    }

    constructor(address vrfCoordinator, address linkToken, bytes32 keyHash, uint fee, address _addr) VRFConsumerBase(vrfCoordinator, linkToken) {
        _keyHash = keyHash;
        _fee = fee;
        bank = Bank(_addr);
    }
    function _random(uint mod) internal view returns (uint) {
        return uint(keccak256(abi.encodePacked(block.prevrandao, block.timestamp))) % mod;
    }
    function _hash() internal view returns (bytes32) {
        return bytes32(keccak256(abi.encodePacked(block.prevrandao, block.timestamp)));
    }
    function place_bet(uint256  _bet_num, uint256 _bet_amounts) public {
        Bet memory bet = Bet(
            msg.sender,
            block.timestamp,
            _bet_num,
            _bet_amounts);

        //chainlink bytes32 req = _hash();  fulfillRandomness
        //bytes32 req = requestRandomness(_keyHash, _fee);
        bytes32 req = _hash();

        bank.subFunds(msg.sender, _bet_amounts);
        bets[req] = bet;

        //
        fulfillRandomness(req, _random(100));
    }
    function _expand(uint randomValue, uint n, uint mod, uint add) internal pure returns (uint[] memory expandedValues) {
        expandedValues = new uint[](n);
        for (uint i = 0; i < n; i++) {
            expandedValues[i] = (uint(keccak256(abi.encode(randomValue, i))) % mod) + add;
        }
        return expandedValues;
    }
    function calcWinner(Bet memory bet,uint[] memory _roll) public pure returns(uint256){
        uint c = 0;
        for(uint i = 0; i < _roll.length; i ++) {
            if(_roll[i] == bet.bet_num) {
                c += 1;
            }
        }

        if(c == 1) {
            // 1:1 
            return bet.bet_amounts * 2;
        } else if(c == 2) {
            // 2:1 
            return bet.bet_amounts * 3;
        } else if(c == 3) {
            // 10:1 
            return bet.bet_amounts * 11;
        }
        return 0;
    }
    function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override  {
        uint[] memory _roll = _expand(randomness, 3, 6, 1);
        rolleds.push(_roll);

        Bet memory bet = bets[requestId];
        uint256 winMoney = calcWinner(bet, _roll);
        if(winMoney>0){
            bank.addFunds(bet.beter, winMoney);
            emit WinFund(bet.beter, winMoney);
        }else{
            //
            // bank.addFunds(address(this), winMoney);
            // emit WinFund(address(this), winMoney);
        }
    }

}

